mapper.select:
select name, email, password
from User
where email = #{email} and password = #{password}

select *
from User
where email = #{email} and oauth = #{oauth}

mapper.update:
update User
set admin = #{admin}, teach = #{teach}, name = #{name}, password = #{password}, oauth = #{oauth}
where email = #{email}

update User
set password = #{password}
where email = #{email}
